let newState = msg.data.event.new_state.attributes;

return {
    acknowledge: true,
    payload: {
        state: {
            thermostatSetPoint: newState.temperature
        }
    }
};

flow.set("delta", msg.payload);
msg.payload = flow.get("setPoint") + msg.payload;

return msg;

return {
    acknowledge: true,
    payload: {
        state: {
            power: "ON"
        }
    }
};

return {
    acknowledge: true,
    payload: {
        state: {
            power: "OFF"
        }
    }
};

let knob = flow.get("knob");

if (flow.get("haKnob") == knob)
{
  return null    
}

flow.set('haKnob', knob);

return {
    payload: knob
};

let encoderValue = flow.get("setPoint") * 2 - 10; 

return { 
    payload: encoderValue
};

if(flow.get("isKnobInhibited") === true){
    return null;
}

return msg;

let newState = msg.data.attributes;

return {
    topic: "setpoint",
    payload: newState.temperature
};

if(!msg.payload.startsWith("== modulation_level")){
    return null;    
}

return {
    topic: "modulation_level",
    payload:  Number(msg.payload.replace(/[^0-9.]/g,''))
};

if(msg.payload > 40){
    return {
        payload: "on " + msg.payload.toFixed(1)
    };
}else{
    return {
        payload: "off"
    };
}

if(!msg.payload.startsWith("== boiler_flow_water_temp")){
    return null;    
}

return {
    topic: "water_temp",
    payload:  Number(msg.payload.replace(/[^0-9.]/g,''))
};
